#--------------------------------------------------------------------------
#Wood Thrush Sampling
#--------------------------------------------------------------------------


#This function establishes the sampling protocol then records which birds were present
#in each circle in each minute of each survey. It then subsets the data pretending each survey
#lasted only 1 minute, generates a simulated point count dataset, fits an occupancy model to
#that simulated data set, and records the coefficients. It then repeats this process for a 2-minute
#survey, a 3-minute survey, etc. until it reaches the maximum survey length. It also records the true
#instantaneous, daily, and seasonal occupancy values from the simulation for comparison with the
#estimates.

#This function takes as inputs:

#(1) spatial information about the survey sites (circleData) and when they are
#occupied by individual Wood Thrush (occData)

#(2) Information about the population being analyzed (density)

#(3) Spatial survey characteristics (radius is the size of the radius of sampling sites,
#and placement identifies whether the sites are placed randomly or in a grid pattern)

#(4) Temporal survey characteristics (nMins is length of each survey,
#intervalLength is the time between surveys, and nSurveys is the total number of surveys)


#The function is used in the for-loop below which iterates the analyses over all
#simulated datasets.

occAnalyses = function(circleData = NULL, radius = NULL, placement = NULL, occData=NULL, nMins=NULL, intervalLength=NULL, nSurveys=NULL, density=NULL){
  
  #Prepare the y data
  yDataPrep = data.frame(sampleTimes) %>% 
    arrange(sampleOrder) %>% 
    left_join(data.frame(circleData), by='sampleOrder', relationship='many-to-many') %>% 
    rename('samplePoint' = 'pointNum') %>% 
    left_join(occData, by=c('samplePoint', 'day'), relationship='many-to-many') %>% 
    mutate(t=t/60) %>% 
    filter(t >= startTime & t < endTime)
  
  #Record true instantaneous, daily, and seasonal occupancy
  instOcc = occData %>% 
    group_by(day, t) %>% 
    summarise(points = n(), occupied = mean(occupied))
  dailyOcc = occData %>% 
    group_by(day, samplePoint) %>% 
    summarise(occupied = max(occupied)) %>% 
    ungroup() %>% 
    group_by(day) %>% 
    summarise(points = n(), occupied = mean(occupied))
  seasonOcc = occData %>% 
    group_by(samplePoint) %>% 
    summarise(occupied = max(occupied)) %>% 
    ungroup() %>% 
    summarise(points = n(), occupied = mean(occupied))
  
  for(j in 1:nMins){
    
    #Creating a y dataframe specific to each of the survey lengths (1 minute, 2 minutes, etc.)
    yData = yDataPrep %>% 
      filter(t >= startTime & t < startTime+j) %>% 
      group_by(surveyLength, interval, numSurveys, x, y, samplePoint, sampleOrder, survey, day, startTime, endTime) %>% 
      summarise(occupiedMins = sum(occupied),
                occupied = max(occupied)) %>% 
      ungroup() %>% 
      mutate(pD = 1-(0.7)^occupiedMins) %>% #30% detect prob/minute
      mutate(unif = runif(nrow(.))) %>% 
      mutate(yData = ifelse(unif < pD, 1, 0)) %>% 
      filter(surveyLength==nMins & interval==intervalLength & numSurveys==nSurveys) %>% 
      pivot_wider(id_cols=samplePoint, names_from=survey, values_from=yData) %>% 
      arrange(samplePoint) %>% 
      select(-samplePoint)
    
    #Fit occupancy model
    res = occu(~1~1, unmarkedFrameOccu(yData))
    
    #Record the coefficients from the fitted model
    tmp1 = data.frame(t(coef(res))) %>%
      setNames(c('psi', 'p')) %>% 
      mutate(surveyLength = j,
             intervalLength = intervalLength,
             nSurveys = nSurveys,
             radius = radius,
             placement=placement) %>% 
      mutate(instOcc = mean(instOcc$occupied),
             dailyOcc = mean(dailyOcc$occupied),
             seasonOcc = seasonOcc$occupied,
             density = density,
             iteration = i)
    
    #Store the results
    if(j == 1){
      results = tmp1
    } else {
      results = rbind(results, tmp1)
    }
  }
  
  return(results)
}


